mapper.select:
SELECT COUNT(*) FROM member_tbl
WHERE mem_id = #{mem_id}
AND mem_pwd = #{mem_pwd}

mapper.update:
   UPDATE  member_tbl
   SET  mem_pwd = #{mem_pwd}
     ,  mem_name = #{mem_name}
     ,  mem_country = #{mem_country}
     ,  mem_birthday = #{mem_birthday}
     ,  mem_comment = #{mem_comment}
     ,  mem_hp =  #{mem_hp}
     ,  mem_email = #{mem_email}
     ,  mem_address = #{mem_address}
	WHERE mem_id = #{mem_id}

mapper.delete:
DELETE FROM member_tbl
WHERE mem_id = #{mem_id}
AND mem_pwd = #{mem_pwd}

mapper.select:
SELECT	mem_status
FROM	member_tbl
WHERE	mem_id=#{mem_id}
